from django.core.management.base import BaseCommand
from production.models import DelitoFuero, DelitoGeneral, DelitoEspecifico, DelitoSubtipo
from django.db import transaction
import csv
import io


class Command(BaseCommand):
    help = 'Crear cat√°logo completo de delitos con fueros'

    def add_arguments(self, parser):
        parser.add_argument(
            '--limpiar',
            action='store_true',
            help='Limpiar datos existentes antes de cargar',
        )

    def handle(self, *args, **options):
        self.stdout.write('üöÄ Iniciando carga de delitos con fueros...')
        
        try:
            with transaction.atomic():
                # Limpiar datos existentes si se solicita
                if options['limpiar']:
                    self.limpiar_datos()
                
                # Verificar que existen los fueros
                if not self.verificar_fueros():
                    self.stdout.write(
                        self.style.WARNING("‚ö†Ô∏è Los fueros no est√°n creados. Cre√°ndolos...")
                    )
                    self.crear_fueros_iniciales()
                
                # Obtener fueros
                fueros = {f.nombre: f for f in DelitoFuero.objects.all()}
                self.stdout.write(f"üìã Fueros disponibles: {list(fueros.keys())}")
                
                # Cargar delitos con fueros
                self.cargar_delitos_con_fueros(fueros)
                
                # Mostrar resumen final
                self.mostrar_resumen_final()
                
        except Exception as e:
            self.stdout.write(
                self.style.ERROR(f"‚ùå Error durante la carga: {e}")
            )
            raise

    def limpiar_datos(self):
        """Limpiar datos existentes"""
        self.stdout.write('üóëÔ∏è Limpiando datos existentes...')
        DelitoSubtipo.objects.all().delete()
        DelitoEspecifico.objects.all().delete()
        DelitoGeneral.objects.all().delete()
        DelitoFuero.objects.all().delete()
        self.stdout.write('‚úÖ Datos limpiados')

    def crear_fueros_iniciales(self):
        """Crear los fueros b√°sicos si no existen"""
        fueros_iniciales = [
            ('FUERO_COMUN', 'Delitos del C√≥digo Penal'),
            ('FUERO_MILITAR_POLICIAL', 'Delitos del Fuero Militar Policial'),
            ('LEYES_ESPECIALES', 'Delitos de Leyes Especiales')
        ]
        
        for nombre, descripcion in fueros_iniciales:
            fuero_obj, created = DelitoFuero.objects.get_or_create(
                nombre=nombre,
                defaults={'descripcion': descripcion}
            )
            if created:
                self.stdout.write(f"‚úÖ Creado fuero: {nombre}")

    def verificar_fueros(self):
        """Verificar que existen los 3 fueros b√°sicos"""
        fueros_requeridos = ['FUERO_COMUN', 'FUERO_MILITAR_POLICIAL', 'LEYES_ESPECIALES']
        fueros_existentes = list(DelitoFuero.objects.values_list('nombre', flat=True))
        
        for fuero in fueros_requeridos:
            if fuero not in fueros_existentes:
                return False
        
        return True

    def cargar_delitos_con_fueros(self, fueros):
        """Cargar delitos usando la estructura con fueros"""
        
        # Obtener datos
        delitos_data = self.get_delitos_data()
        
        delitos_generales = {}
        delitos_especificos = {}
        contador_progress = 0
        total_records = len(delitos_data)
        
        self.stdout.write(f'üìã Procesando {total_records} registros de delitos...')
        
        for fuero_str, delito_general_str, delito_especifico_str, sub_tipo_str in delitos_data:
            contador_progress += 1
            
            # Limpiar textos
            fuero_str = fuero_str.strip()
            delito_general_str = delito_general_str.strip()
            delito_especifico_str = delito_especifico_str.strip()
            sub_tipo_str = sub_tipo_str.strip()
            
            # Obtener objeto fuero
            try:
                fuero_obj = fueros[fuero_str]
            except KeyError:
                self.stdout.write(f"‚ùå Fuero no encontrado: {fuero_str}")
                continue
            
            # Crear o obtener Delito General
            clave_general = f"{fuero_str}|{delito_general_str}"
            
            if clave_general not in delitos_generales:
                delito_general_obj, created = DelitoGeneral.objects.get_or_create(
                    delito_fuero=fuero_obj,
                    nombre=delito_general_str
                )
                delitos_generales[clave_general] = delito_general_obj
                if created:
                    self.stdout.write(f"‚úÖ Creado delito general: {fuero_str} - {delito_general_str}")
            else:
                delito_general_obj = delitos_generales[clave_general]
            
            # Crear o obtener Delito Espec√≠fico
            clave_especifico = f"{clave_general}|{delito_especifico_str}"
            
            if clave_especifico not in delitos_especificos:
                delito_especifico_obj, created = DelitoEspecifico.objects.get_or_create(
                    delito_general=delito_general_obj,
                    nombre=delito_especifico_str
                )
                delitos_especificos[clave_especifico] = delito_especifico_obj
                if created:
                    self.stdout.write(f"  ‚û°Ô∏è Creado delito espec√≠fico: {delito_especifico_str}")
            else:
                delito_especifico_obj = delitos_especificos[clave_especifico]
            
            # Crear Sub Tipo Delito
            sub_tipo_obj, created = DelitoSubtipo.objects.get_or_create(
                delito_especifico=delito_especifico_obj,
                nombre=sub_tipo_str
            )
            
            if created:
                self.stdout.write(f"    üî∏ Creado sub tipo: {sub_tipo_str}")
            
            # Mostrar progreso cada 50 registros
            if contador_progress % 50 == 0:
                porcentaje = (contador_progress / total_records) * 100
                self.stdout.write(f"üìä Progreso: {contador_progress}/{total_records} ({porcentaje:.1f}%)")

    def mostrar_resumen_final(self):
        """Mostrar resumen final de la carga"""
        self.stdout.write('\n' + '='*80)
        self.stdout.write(
            self.style.SUCCESS("üéâ ¬°Carga de delitos completada exitosamente!")
        )
        self.stdout.write('\nüìä Resumen de carga:')
        self.stdout.write(f"‚úÖ Fueros: {DelitoFuero.objects.count()}")
        self.stdout.write(f"‚úÖ Delitos Generales: {DelitoGeneral.objects.count()}")
        self.stdout.write(f"‚úÖ Delitos Espec√≠ficos: {DelitoEspecifico.objects.count()}")
        self.stdout.write(f"‚úÖ Sub Tipos de Delito: {DelitoSubtipo.objects.count()}")
        
        # Resumen por fuero
        self.stdout.write('\nüìà Distribuci√≥n por fuero:')
        for fuero in DelitoFuero.objects.all():
            count_generales = DelitoGeneral.objects.filter(delito_fuero=fuero).count()
            count_especificos = DelitoEspecifico.objects.filter(delito_general__delito_fuero=fuero).count()
            count_subtipos = DelitoSubtipo.objects.filter(delito_especifico__delito_general__delito_fuero=fuero).count()
            
            self.stdout.write(f"  üèõÔ∏è {fuero.nombre}:")
            self.stdout.write(f"    üìã {count_generales} delitos generales")
            self.stdout.write(f"    üìÑ {count_especificos} delitos espec√≠ficos")
            self.stdout.write(f"    üî∏ {count_subtipos} sub tipos")

